import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Question from "./Question";
import {nanoid} from "nanoid"


function Quiz() {
    const [questions, setQuestions] = useState([]);
    const questionAmount = 5;
    var defaultPlayerAnswers = [];

    for (let x = 0; x < questionAmount; x++) {
        defaultPlayerAnswers.push({questionNum: x, answer:false});
    }

    const [playersAnswers, setPlayersAnswers] = useState(defaultPlayerAnswers);

    useEffect(() => {
        let q = fetch(`https://opentdb.com/api.php?amount=${questionAmount}`)
            .then(res => res.json())
            .then(data => setQuestions(data.results))
        },[])

    function handleQuestion(event, playerAnswer, key){
        setPlayersAnswers(prev => playersAnswers.map(answer =>{
           return answer.questionNum === key ? 
                {...answer, answer:playerAnswer} :
                answer;
            }))

    }

