import React from "react";
import { useState } from "react/cjs/react.development";

function Splash({playerName, handleName}) {
  cosnt [firstQuestion, setFirstQuestion] = useState([]);
  
    return (
      <div className="App">
         <div className="container">
            <h1 className="title">Quiz</h1>
            <p className="sub-text">Take the general knowledge quiz</p>
            <input className="playerName" value={playerName} onChange={e=> handleName(e)} name="playerName" />
            <a href="quiz"><button className="start-button">Start quiz</button></a>
            <a href="highscore"><button className="start-button">Local Highscore</button></a>
            </div>
      </div>
    );
  }
  
  export default Splash;
