import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Question from "./Question";
import {nanoid} from "nanoid"


function Quiz() {
    const [questions, setQuestions] = useState([]);
    const [quizComplete, setQuizComplete] = useState(false)
    const [questionComplete, setQuestionComplete] = useState(false)
    const [correctAnswers, setcorrectAnswers] = useState(0)
    const [restartGame, setRestartGame] = useState(0)
    
    const questionAmount = 1;
    var defaultPlayerAnswers = [];

    for (let x = 0; x < questionAmount; x++) {
        defaultPlayerAnswers.push({questionNum: x, answer:false});
    }

    const [playersAnswers, setPlayersAnswers] = useState(defaultPlayerAnswers);

    useEffect(() => {
        let q = fetch(`https://opentdb.com/api.php?amount=${questionAmount}`)
            .then(res => res.json())
            .then(data => setQuestions(data.results))
        },[restartGame])

    function handleQuestion(event, playerAnswer, key){
        setQuestionComplete(true);
        console.log(questions[0])
    }

    function checkScores(){
        setcorrectAnswers(0)
        playersAnswers.map(answer =>{
            return answer.answer === questions[answer.questionNum].correct_answer ? setcorrectAnswers(prev=>prev+1) : correctAnswers;
        })
        setQuizComplete(true) 
    }

    function handleSubmission(){
        quizComplete ?
        newGame(): 
        checkScores() ;
    }

    function newGame(){
        setPlayersAnswers(defaultPlayerAnswers)
        setQuizComplete(false);
        setRestartGame(prev=>prev+1)
        console.log(defaultPlayerAnswers);
    }



    return (
        <div>
            <div className="container">
                {questions.map((question,key) => {
                    return <Question key={key} id={key} playerAnswer={playersAnswers[`${key}`]} 
                    handleQuestion={handleQuestion} question={question.question} correct={question.correct_answer} 
                    incorrect={question.incorrect_answers} category={question.category}
                    questionComplete={questionComplete}/>
                })}

                <button onClick={handleSubmission}>{quizComplete ? 'Play Again' : 'Check your scores!'}</button>
                <a href="/"><button>Back to main menu</button></a>

                <h4>{quizComplete && `${correctAnswers} / ${questionAmount}` }</h4>

            </div>
        </div>
    );
}

export default Quiz;
