import React from "react";
import { useState, useEffect } from "react/cjs/react.development";

function Splash({playerName, handleName}) {
  const [firstQuestion, setFirstQuestion] = useState([]);

  useEffect(() => {
    let q = fetch(`https://opentdb.com/api.php?amount=1&type=multiple`)
        .then(res => res.json())
        .then(data =>setFirstQuestion(data.results[0]))
    },[])

    return (
      <div className="App">
         <div className="container">
            <h1 className="title">Quiz</h1>
            <p className="sub-text">Take the general knowledge quiz</p>
            <input className="playerName" value={playerName} firstQuestion={firstQuestion} onChange={e=> handleName(e)} name="playerName" />
            <a href="quiz"><button className="start-button">Start quiz</button></a>
            <a href="highscore"><button className="start-button">Local Highscore</button></a>
            </div>
      </div>
    );
  }
  
  export default Splash;
