import React from "react";

function ScoreInformation({score, name}) {

    const savedScores = localStorage.getItem("score");
    const savedObj = JSON.parse(savedScores)
    const playerBestScore = savedObj.filter((score) => score.name == name);
    let highestScore = savedObj.reduce((highest, game) => highest.score > game.score ? highest : game);
    console.log(playerBestScore)

  

    return (
     <div className="score__container">
         <p>Name: {name}</p>
         <p>Score: {score}</p>
         <p>Your highest score: {playerBestScore.length? playerBestScore[0].score: '0'}</p>
         <p>Top score: {highestScore.name} ({highestScore.score})</p>
     </div>
    );
  }
  
  export default ScoreInformation;
