import React from "react";

function ScoreInformation({score, name}) {

    const savedScores = localStorage.getItem("score");
    const savedObj = JSON.parse(savedScores)
    const playerBestScore = savedObj.filter((score) => score.name == name);
    const highestScore = savedObj.reduce(function(max, savedObj) { 
        return Math.max(max, savedObj); 
    }, -Infinity)

    console.log(highestScore)

  

    return (
     <div>
         Name: {name}
         Score:{score}
     </div>
    );
  }
  
  export default ScoreInformation;
