import React, { useEffect } from "react";
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { useState } from "react/cjs/react.development";



function Question({question, correct, incorrect, category, handleQuestion, playerAnswer, questionComplete, fiftyFifty}) {

  const [shuffAnswers, setShuffAnswers] = useState(false)
  useEffect(() => {
    playerAnswer ? 
      updateAnswers():
    fiftyFifty?
      fiftyFifty():
    shuffleAnswers();

},[question, fiftyFifty, playerAnswer,questionComplete])


function shuffleAnswers(){
  let answers = fiftyFifty? [incorrect[Math.floor(Math.random() * 2)],correct] : incorrect.concat(correct);
  const shuffledArray = !fiftyFifty&& answers.sort((a, b) => 0.5 - Math.random());
  setShuffAnswers(shuffledArray.map(ans=>({
    answer:ans, 
    class: 'answer__button'
  })))
}

function fiftyFifty(){
  let answers = [incorrect[Math.floor(Math.random() * 2)],correct]
  setShuffAnswers(shuffledArray.map(answers))
}

function updateAnswers(){
  setShuffAnswers(oldAnswers => oldAnswers.map(ans => {
    return ans.answer === correct ?
    {...ans, class:questionComplete? 'answer__button green' : ans.answer === playerAnswer? 'answer__button blue' : 'answer__button'} :
      ans.answer === playerAnswer ? 
        {...ans, class:'answer__button blue'} :
        ans
     
}))
}

// function updateAnswers(){
//   setShuffAnswers(oldAnswers => oldAnswers.map(ans => {
//     console.log(ans.answer === correct)
//     return {...ans, class: 'answer__button green'} 
//   }))
// }




return (
      <div className="question__card">
      <h5 className="question__category">{category}</h5>
       
          
        <div className="question" dangerouslySetInnerHTML ={{__html: question}}></div>
        <div className="answers">
        {shuffAnswers && shuffAnswers.map(answer=>{
            return <button dangerouslySetInnerHTML ={{__html: answer.answer}} 
            onClick={e => handleQuestion(e, answer.answer )} 
            className={answer.class}>
                
                </button>
        })}
        </div>
      </div>
    );
  }
  
  export default Question;
