import React, { useEffect } from "react";
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { useState } from "react/cjs/react.development";



function Question({question, correct, incorrect, category, handleQuestion, questionComplete, fiftyFifty}) {
  const [shuffAnswers, setShuffAnswers] = useState(false)

  useEffect(() => {
    setShuffAnswers(false)
    let answers = fiftyFifty? [incorrect[Math.floor(Math.random() * 2)],correct] : incorrect.concat(correct);
    const shuffledArray = answers.sort((a, b) => 0.5 - Math.random());
    !shuffAnswers && setShuffAnswers(shuffledArray)
    console.log(shuffAnswers)

},[question])

    
    
    return (
      <div className="question">
          <h3>{category}</h3>
        <div dangerouslySetInnerHTML ={{__html: question}}></div>
        <div className="answers">
        {shuffAnswers && shuffAnswers.map(answer=>{
            return <button dangerouslySetInnerHTML ={{__html: answer}} 
            onClick={e => handleQuestion(e, answer )} 
            className={
              questionComplete &&
              answer === correct ? 'green answer' : ''
            }>
                
                </button>
        })}
        </div>
      </div>
    );
  }
  
  export default Question;
