import React from "react";
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';


function Question({question, correct, incorrect, category, handleQuestion, id, playerAnswer}) {
    let answers = incorrect.concat(correct);
    return (
      <div className="question">
          <h3>{category}</h3>
        <h4>{JSON.stringify(question)}</h4>
        <div className="answers">
        {answers.map(answer=>{
            return <button onClick={e => handleQuestion(e, answer, id )} className={playerAnswer.answer === answer ? 'green answer': 'white answer' }>{answer}</button>
        })}
        </div>
      </div>
    );
  }
  
  export default Question;
