import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Question from "./Question";


function Quiz() {
    const [questions, setQuestions] = useState([]);
    const [playersAnswers, setPlayersAnswers] = useState([]);


    useEffect(() => {
        fetch("https://opentdb.com/api.php?amount=5")
            .then(res => res.json())
            .then(data => setQuestions(data.results))
    },[])

    function handleQuestion(){
        console.log('handlin')
    }




    return (
        <div>
            <div className="container">
                {questions.map((question) => {
                    return <Question handleQuestion={handleQuestion} question={question.question} correct={question.correct_answer} incorrect={question.incorrect_answers} category={question.category}/>
                })}
                <a href="/"><button>Back to main menu</button></a>

            </div>
        </div>
    );
}

export default Quiz;
