import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Question from "./Question";
import ScoreInformation from "./ScoreInformation";
import {nanoid} from "nanoid"


function Quiz({playerName}) {
    const [question, setQuestion] = useState(false);
    const [questionsList, setQuestionsList] = useState([]);
    const [quizComplete, setQuizComplete] = useState(false)
    const [questionComplete, setQuestionComplete] = useState(false)
    const [correctAnswers, setcorrectAnswers] = useState(0)
    const [playerAnswer, setPlayerAnswer] = useState(false)
    const [newQuestion, setNewQuestion] = useState(0)
    const [fiftyFifty, setFiftyFifty] = useState(false)
    const [fiftyFiftyStatus, setFiftyFiftyStatus] = useState(true)
    const [score, setScore] = useState({
        name: playerName,
        score: 0

    })

    // User has switched back to the tab
        const onFocus = () => {
            console.log("Tab is in focus");
        };

        // User has switched away from the tab (AKA tab is hidden)
        const onBlur = () => {
            console.log("Tab is blurred");
        };

    const WindowFocusHandler = () => {
        useEffect(() => {
            window.addEventListener("focus", onFocus);
            window.addEventListener("blur", onBlur);
            // Calls onFocus when the window first loads
            onFocus();
            // Specify how to clean up after this effect:
            return () => {
                window.removeEventListener("focus", onFocus);
                window.removeEventListener("blur", onBlur);
            };
      }, []);
    
        return <></>;
    };
    
    const questionAmount = 1;
        useEffect(() => {
            let q = fetch(`https://opentdb.com/api.php?amount=1&type=multiple`)
                .then(res => res.json())
                .then(data =>setQuestion(data.results[0]))
            },[])
        useEffect(() => {
            let q = fetch(`https://opentdb.com/api.php?amount=50&type=multiple`)
                .then(res => res.json())
                .then(data =>setQuestionsList(data.results))
            },[])
        useEffect(() => {
                setQuestion(questionsList[0]);
                setPlayerAnswer(false);
                setQuestionsList(prevList =>{
                    prevList.splice(0,1)
                    return prevList
                })
        },[newQuestion])


        async function handleQuestion(event, playerAnswer){
            console.log(questionComplete)
            if(!questionComplete){ 
               
            }
        }

        async function handleQuestion(event, playerAnswer) {
            if (questionComplete) return;
            console.log('you should not be here')
            setPlayerAnswer(playerAnswer)
            await timeout(1000);
            setQuestionComplete(true);
            await timeout(2000);
            question.correct_answer === playerAnswer ?
            correctAnswer():
            incorrectAnswer()
            fiftyFifty && setFiftyFiftyStatus(false)
            setFiftyFifty(false)
         }

        function handleFiftyFifty(){
            setFiftyFifty(true);
        }

        function timeout(delay) {
            return new Promise( res => setTimeout(res, delay) );
        }

        async function incorrectAnswer(){
            setQuizComplete(true)
            saveScoreLocal()
        }

        function saveScoreLocal(){
            const savedScores = localStorage.getItem("score");
            const savedObj = JSON.parse(savedScores)

        var exists = savedObj && 
            Object.keys(savedObj).some(function(k) {
                return savedObj[k].name === score.name;
            });
        
            exists ?
            saveCurrentPlayerScore(savedObj)
            :
                savedObj ?
                    localStorage.setItem("score", JSON.stringify([
                        ...savedObj,
                        score
                    ])) 
                :
                    localStorage.setItem("score", JSON.stringify([
                        score
                    ]))

        }

        function saveCurrentPlayerScore(savedObj){
            savedObj.map(savedScore=>{
                if(savedScore.name === score.name){
                    savedScore.score = score.score > savedScore.score ? 
                    score.score :
                    savedScore.score
                    
                }
            }) 

            localStorage.setItem("score", JSON.stringify([
                ...savedObj
            ]))
        }


    async function correctAnswer(){
            setNewQuestion(prev=>prev+1)
            setScore(prev=>({
                ...prev,
                score:prev.score+1
            }));
            setQuestionComplete(false)
        
        }
   
        function handleSubmission(){
            quizComplete &&
            newGame()
        }

        function newGame(){
            setQuizComplete(false);
            setQuestionComplete(false)
            setFiftyFiftyStatus(true)
            setNewQuestion(prev=>prev+1)
            setScore(prev=>({
                ...prev,
                score:0
            }));
        }
    return (
        <div>
            <div className="container">
                    <ScoreInformation name={score.name}  score={score.score} />
                    {question && <Question  
                    handleQuestion={handleQuestion} question={question.question} correct={question.correct_answer} 
                    incorrect={question.incorrect_answers} category={question.category}
                    questionComplete={questionComplete} playerAnswer={playerAnswer} fiftyFifty={fiftyFifty}/> }
                    
                    <div class="menu__items">
                        <div className="lifelines__container">
                                <h4>Lifelines:</h4>
                                <div className="lifelines__list">
                                    {fiftyFiftyStatus && <button className="lifeLine" onClick={handleFiftyFifty}>50/50</button> }
                                </div>
                        </div>
                        <div className="menu__container">
                            {quizComplete && <button className="question__menu__button" onClick={handleSubmission}>Play Again</button> }
                            <a href="/"><button className="question__menu__button">Back to main menu</button></a>
                        </div>
                    </div>
                </div>
        </div>
    );
}

export default Quiz;
