import Quiz from "./components/Quiz";
import Splash from "./components/Splash";
import Highscore from "./components/Highscore";
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { useState, useEffect } from "react/cjs/react.development";


function App() {

  const [playerName,SetPlayerName ] = useState(localStorage.getItem("name") ? localStorage.getItem("name") : '')
  const [firstQuestion, setFirstQuestion] = useState({});
  function handleName(e){
    SetPlayerName(e.target.value);
    localStorage.setItem("name",e.target.value) 
  }
  useEffect(() => {
    let q = fetch(`https://opentdb.com/api.php?amount=1&type=multiple`)
        .then(res => res.json())
        .then(data =>localStorage.setItem("firstQuestion",JSON.stringify(data.results)))
    },[])

    console.log(localStorage.getItem("firstQuestion"))



    return (
    <div className="App">
       {firstQuestion && <Router>
        <Routes>
          <Route path="/" element={<Splash handleName={handleName} playerName={playerName} />} />
          <Route path="quiz" element={<Quiz playerName={playerName} firstQuestion={firstQuestion}  />} />
          <Route path="highscore" element={<Highscore />} />
        </Routes>
      </Router>}
    </div>
  );
}

export default App;
